// 2nd slider js code 
let currentSlide = 0;
let totalSlides = 3;
let slideInterval;

function prevSlide() {
  currentSlide--;
  if (currentSlide < 0) {
    currentSlide = totalSlides - 1;
  }
  showSlide();
}

function nextSlide() {
  currentSlide++;
  if (currentSlide >= totalSlides) {
    currentSlide = 0;
  }
  showSlide();
}

function showSlide() {
  let slides = document.getElementsByClassName('slidE');
  for (let i = 0; i < slides.length; i++) {
    slides[i].classList.remove('active');
  }
  slides[currentSlide].classList.add('active');
}

slideInterval = setInterval(nextSlide, 3000);

// our brand slider
function autoPlaySlider() {
  let slidesContainer = document.querySelector('.brand_slides');
  let slides = Array.from(slidesContainer.children);
  let slideWidth = slides[0].offsetWidth;
  let currentSlide = 0;

  setInterval(function () {
    // Move to the next slide
    currentSlide = (currentSlide + 1) % slides.length;
    let offset = currentSlide * slideWidth;
    slidesContainer.style.transition = 'transform 0.5s ease';
    slidesContainer.style.transform = 'translateX(-' + offset + 'px)';
  }, 3000); // Change slide every 3 seconds (adjust as needed)

  slidesContainer.addEventListener('transitionend', function () {
    // Reset transition and move the first slide to the end
    if (currentSlide === slides.length - 1) {
      slidesContainer.style.transition = 'none';
      slidesContainer.style.transform = 'translateX(0)';
      currentSlide = 0;
      // console.log(0);
    }
  });
}

autoPlaySlider();

