// Array of text values
var texts = ["Enjoy exclusive savings with our limited-time discount offer, making your purchase even more affordable while supplies last!", "Treat yourself to a fantastic deal and indulge in discounted prices, giving you more reasons to enjoy our products/services without breaking the bank.", "Enjoy exclusive savings with our limited-time discount offer, making your purchase even more affordable while supplies last!", "Treat yourself to a fantastic deal and indulge in discounted prices, giving you more reasons to enjoy our products/services without breaking the bank."];

var currentIndex = 0; // Current index of the text array

// Function to update the text
function updateText() {
  var textElement = document.getElementById("text");
  textElement.textContent = texts[currentIndex];
}

// Event listener for previous button click
document.getElementById("prevBtn").addEventListener("click", function() {
  if (currentIndex > 0) {
    currentIndex--;
  } else {
    currentIndex = texts.length - 1;
  }
  updateText();
});

// Event listener for next button click
document.getElementById("nextBtn").addEventListener("click", function() {
  if (currentIndex < texts.length - 1) {
    currentIndex++;
  } else {
    currentIndex = 0;
  }
  updateText();
});

// Initial text update
updateText();
