// open and close profile items
function userProfleButton() {
  const content = document.getElementById('profileContent');
  if (content.style.display === 'none') {
    content.style.display = 'block';
  } else if (content.style.display) {
    content.style.display = 'none';
  } else {
    content.style.display = 'block';
  }

}
// navbar menu item show and hidden 
function mobileMenuToggle() {
  const content = document.getElementById('mobile-menu');
  if (content.style.display === 'none') {
    content.style.display = 'block';
  } else if (content.style.display) {
    content.style.display = 'none';
  } else {
    content.style.display = 'block';
  }

}
// slider 
// Array of text values
let texts = ["Enjoy exclusive savings with our limited-time discount offer, making your purchase even more affordable while supplies last!", "Treat yourself to a fantastic deal and indulge in discounted prices, giving you more reasons to enjoy our products/services without breaking the bank.", "Enjoy exclusive savings with our limited-time discount offer, making your purchase even more affordable while supplies last!", "Treat yourself to a fantastic deal and indulge in discounted prices, giving you more reasons to enjoy our products/services without breaking the bank."];

let currentIndex = 0; // Current index of the text array

// Function to update the text
function updateText() {
  let textElement = document.getElementById("text");
  textElement.textContent = texts[currentIndex];
}

// Event listener for previous button click
document.getElementById("prevBtn").addEventListener("click", function() {
  if (currentIndex > 0) {
    currentIndex--;
  } else {
    currentIndex = texts.length - 1;
  }
  updateText();
});

// Event listener for next button click
document.getElementById("nextBtn").addEventListener("click", function() {
  if (currentIndex < texts.length - 1) {
    currentIndex++;
  } else {
    currentIndex = 0;
  }
  updateText();
});

// Initial text update
updateText();

// 2nd slider js code 
let currentSlide = 0;
let totalSlides = 3;
let slideInterval;

function prevSlide() {
  currentSlide--;
  if (currentSlide < 0) {
    currentSlide = totalSlides - 1;
  }
  showSlide();
}

function nextSlide() {
  currentSlide++;
  if (currentSlide >= totalSlides) {
    currentSlide = 0;
  }
  showSlide();
}

function showSlide() {
  let slides = document.getElementsByClassName('slidE');
  for (let i = 0; i < slides.length; i++) {
    slides[i].classList.remove('active');
  }
  slides[currentSlide].classList.add('active');
}

slideInterval = setInterval(nextSlide, 10000);

// our brand slider
function autoPlaySlider() {
  let slidesContainer = document.querySelector('.brand_slides');
    let slides = Array.from(slidesContainer.children);
    let slideWidth = slides[0].offsetWidth;
    let currentSlide = 0;
  
    setInterval(function() {
      // Move to the next slide
      currentSlide = (currentSlide + 1) % slides.length;
      let offset = currentSlide * slideWidth;
      slidesContainer.style.transition = 'transform 0.5s ease';
      slidesContainer.style.transform = 'translateX(-' + offset + 'px)';
    }, 3000); // Change slide every 3 seconds (adjust as needed)
  
    slidesContainer.addEventListener('transitionend', function() {
      // Reset transition and move the first slide to the end
      if (currentSlide === slides.length - 1) {
        slidesContainer.style.transition = 'none';
        slidesContainer.style.transform = 'translateX(0)';
        currentSlide = 0;
        // console.log(0);
      }
    });
  }
  
  autoPlaySlider();
  