// anther slider
// Get the necessary elements
const sliderWrapper = document.querySelector('.slider-wrapper');
const prevButton = document.querySelector('.prev-button');
const nextButton = document.querySelector('.next-button');

// Set initial variables
let antherCurrentIndex = 0;
const slideCount = sliderWrapper.children.length;

// Function to slide to the next index
const slideNext = () => {
  antherCurrentIndex = (antherCurrentIndex + 1) % slideCount;
  updateSlider();
};

// Function to slide to the previous index
const slidePrev = () => {
  antherCurrentIndex = (antherCurrentIndex - 1 + slideCount) % slideCount;
  updateSlider();
};

// Function to update the slider position
const updateSlider = () => {
  const translateX = -antherCurrentIndex * 100;
  sliderWrapper.style.transform = `translateX(${translateX}%)`;
};

// Attach click event listeners to the buttons
prevButton.addEventListener('click', slidePrev);
nextButton.addEventListener('click', slideNext);

// Auto slide change every 5 seconds
setInterval(slideNext, 10000);


